<template>
  <div class="home">
    <h4>Display</h4>
    <div id="roomdisplay">
      <live-view v-show="liveUrl" :px-width="240" :px-height="160"></live-view>
      <img v-show="!liveUrl && zoomSharingPc" title="Sharing PC" class="displaycontent" src="data:image/png;base64,">
      <img v-show="!liveUrl && !zoomSharingPc && zoomSessionRunning" title="Meeting Camera view" class="displaycontent" src="data:image/jpeg;base64,">
      <img v-show="!liveUrl && !zoomSharingPc && !zoomSessionRunning" title="Digital Signage" class="displaycontent" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSlFT3NqfII2a2pTJQyBGZT5uiXpxTzpQ1uc188CjHtm50TGRz5cw">
    </div>
    <h4>Zoom Rooms UI (simplified)</h4>
    <div id="roomui">
      <div class="uicontainer" v-if="zoomSessionRunning">
        <div id="endmeeting" @click="endZoomMeeting">
          <img class="endmeetingicon" src="../assets/leave_meeting_icon.png">
        </div>
        <div id="sharemypc" @click="toggleSharePc">
          <img class="sharemypcicon" src="../assets/share_my_pc_icon.png">
        </div>
      </div>
      <div class="uicontainer" v-else>
        <div id="startmeeting" @click="startZoomMeeting">
          <img class="startmeetingicon" src="../assets/start_meeting_icon.png">
        </div>
      </div>
      <div class="cpcontainer">
        <control-pad></control-pad>
      </div>
    </div>
    <ul id="pdflinkbox">
      <li v-for="pdfUrl in pdfLinks" :key="pdfUrl">
        <a :href="pdfUrl" target="_blank">{{pdfUrl}}</a>
      </li>
    </ul>

  </div>
</template>

<script>
  import { mapGetters, mapActions } from 'vuex'
  import ControlPad from '../components/ControlPad.vue'
  import LiveView from '../components/LiveView.vue'

  export default {
    mounted() {
      if (!this.isPaired) {
        this.$router.push('pair');
      }
    },
    computed: {
      ...mapGetters([
        'kaptivoId',
        'isPaired',
        'liveUrl',
        'sessionId',
        'zoomSessionRunning',
        'zoomSharingPc',
        'pdfLinks',
      ]),
    },
    watch: {
      liveUrl (val) {
        if (val) {
          this.stopSharingPcOnZoom();
        }
      },
    },
    methods: {
      ...mapActions([
        'setMessage',
        'startZoomMeeting',
        'endZoomMeeting',
        'stopSharingPcOnZoom',
        'toggleSharePc',
      ]),
    },
    components: {
      ControlPad,
      LiveView,
    },
  }
</script>


